news.ycombinator.com.css:
html, body {
  background-color: #f2f2f2;
}
#hnmain {
  min-width: unset;
  max-width: 50rem;
  width: 100%;
  background-color: #fcfcfc;
  padding: 0;
  border-radius: 4px;
  box-shadow: 0 0 10px #ccc;
  overflow: hidden;
}

#hnmain > tbody > tr:first-child td * {
  font-size: 12px !important;
}

#hnmain > tbody > tr:nth-child(3) {
  padding: 1rem 2rem;
  display: block;
}

td {
  font-size: 16px;  
}

.comment {
  font-size: 16px;
  margin-bottom: 1rem;
}

.comment span {
  color: #444;
}

.comment span a {
  color: #356AA0;
}

tr.athing:not(:first-child) td {
  padding-top: 0.5rem;  
}

tr.athing td:first-child {
  padding-right: 0.75rem;
}

tr.athing td:nth-child(2) {
  padding-right: 0.25rem;
}

.subtext {
  font-size: 12px;
  padding-top: 0.25rem;
}
